import React, { useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Login() {
    const usernameInput = useRef()
    const passwordInput = useRef()
    const navigate = useNavigate();

    function processLogin(e){
        const usernameText = usernameInput.current.value
        const passwordText = passwordInput.current.value

        if(usernameText.length !== 0 && passwordText.length !== 0){
            const requestOptions = {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json'},
                body:JSON.stringify({ "username": usernameText, "password": passwordText })
            };

            fetch('http://localhost:9000/users/login', requestOptions)
            .then(res => {if(res.status === 201) directToChatPage(usernameText)})
        }
    }

    useEffect(() => {
        if(localStorage.getItem('token') !== null)
            navigate("/chatpage")
    });

    function directToRegister(e){
        navigate("/register")
    }

    function directToChatPage(username){
        localStorage.setItem('token', username)
        navigate("/chatpage")
    }

    return (
        <>
            <div style={{
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "blue"
  }}>LOGIN PAGE</div>
            <div>Username</div>
            <input ref={usernameInput} type ="text"/>
            <div>Password</div>
            <input ref={passwordInput} type ="password"/>
            <Button variant="primary" className="btn-primary" onClick={processLogin}>Login</Button>
            <br></br>
            <Button onClick={directToRegister}>Register</Button>
        </>
    )
}
