import React, { useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from 'react-bootstrap';

export default function ChatPage() {
    const navigate = useNavigate();
    const webSocket = useRef(null);
    const chatInput = useRef()

    useEffect(() => {
        if(localStorage.getItem('token') === null)
            navigate("/") 
        webSocket.current = new WebSocket('ws://localhost:8080/ChatServer/ws');
        webSocket.current.onopen = () => {
            console.log('WebSocket Client Connected');
        };
    
        webSocket.current.onmessage = (message) => {
            console.log(message);
        };
        webSocket.current.onerror = function() {
            console.log('Connection Error');
        };
    }, []);

    function registerMessage(e){
        console.log("AAAA");
    }

    return (
        <>
            <div>CHAT PAGE</div>
            <div class="centerMiddle">
                <div class="logo"><b>CHAT</b></div>
                <div class="chatForm">
                <input class="form-control inputs" ref={chatInput} placeholder="Chat here!" required/> 
                <Button className="loginButton btn-secondary" onClick={registerMessage}>Send</Button>
                </div>       
            </div>
            <button onClick={logOut}>Logout</button>
        </>
    )

    function logOut(e){
        localStorage.clear()
        webSocket.current.close(); 
        navigate("/")
    }
}
