import React, { useEffect, useRef, useState} from "react";
import { useNavigate } from "react-router-dom";
import { Button } from 'react-bootstrap';

export default function ChatPage() {
    const navigate = useNavigate();
    const webSocket = useRef(null);
    const chatInput = useRef()
    const recieverInput = useRef()
    const [leftContent, setLeftContent] = useState([]);
    const [rightContent, setRightContent] = useState([]);
    const [sessionID, setSessionID] = useState("");
    let fff = useRef();

    useEffect(() => {
        if(localStorage.getItem('token') === null)
            navigate("/") 
        webSocket.current = new WebSocket('ws://localhost:8080/ChatServer/ws');
        webSocket.current.onopen = () => {
            webSocket.current.send("SESSIONID");
            console.log('WebSocket Client Connected');
        };
    
        webSocket.current.onmessage = (message) => {
            const json = JSON.parse(message.data);
            if(json.purpose === "SESSIONID"){
                setSessionID(json.message);
            }
            else{
                localStorage.setItem("rightContent", localStorage.getItem("rightContent") + json.message);
            }

        };
        webSocket.current.onerror = function() {
            console.log('Connection Error');
        };
    }, []);

    function messageToAll(e){
        const messageText = chatInput.current.value
        setLeftContent(leftContent + messageText + "\n");
        const requestOptions = {
            method: 'POST',
            headers: { 
                'Content-Type': 'application/json'},
            body:JSON.stringify({ "message": messageText, "sessionID": sessionID, "sender": localStorage.getItem('token') })
        };

        fetch('http://localhost:8080/ChatServer/messages/all', requestOptions)
        .then(res => res.text())
        .then(res => console.log(res))
    }
    function privateMessage(e){

        return;
    }

    function registerMessage(e){
        if(recieverInput.current.value === ""){
            messageToAll(e);
        }
        else privateMessage(e);
    }

    return (
        <>
            <div>CHAT PAGE</div>
            <div class="centerMiddle">
                <div class="logo"><b>CHAT</b></div>
                <div class="chatForm" id="chatForm">
                    <p class="textLeft">{leftContent}</p>
                    <p class="textRight">{localStorage.getItem("rightContent")}</p>
                </div>    
            </div>
            <div class="chattingFunctionalities">
                    <div class="sideBySide">
                        <input class="form-control inputs inputChat" ref={chatInput} placeholder="Chat here!" required/> 
                        <input class="form-control inputs inputReciever" ref={recieverInput} placeholder="All" required/> 
                    </div>
                    <Button className="loginButton btn-secondary" onClick={registerMessage}>Send</Button>
                    </div>
            <button onClick={logOut}>Logout</button>
        </>
    )

    function logOut(e){
        localStorage.clear()
        webSocket.current.close(); 
        navigate("/")
    }
}
