import React, { useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from 'react-bootstrap';

export default function Login() {
    const usernameInput = useRef()
    const passwordInput = useRef()
    const navigate = useNavigate();

    function processLogin(e){
        const usernameText = usernameInput.current.value
        const passwordText = passwordInput.current.value

        if(usernameText.length !== 0 && passwordText.length !== 0){
            const requestOptions = {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json'},
                body:JSON.stringify({ "username": usernameText, "password": passwordText })
            };

            fetch('http://localhost:9000/users/login', requestOptions)
            .then(res => {if(res.status === 201) directToChatPage(usernameText)})
        }
    }

    useEffect(() => {
        if(localStorage.getItem('token') !== null)
            navigate("/chatpage")
    });

    function directToRegister(e){
        navigate("/register")
    }

    function directToChatPage(username){
        localStorage.setItem('token', username)
        navigate("/chatpage")
    }

    const handleChange = (event) => {
    
    }

    return (
        <>
            <div class="centerMiddle">
                <div class="logo"><b>CHIT-CHAT</b></div>
                <div class="formBack">
                    <div class="header">Login</div>
                    <br></br>
                    <input  class="form-control inputs" ref={usernameInput} type="text" placeholder="Username" maxlength="22" required onChange={handleChange}/>
                    <br></br>
                    <input class="form-control inputs" ref={passwordInput} type="password" placeholder="Password" maxlength="27" required/>
                    <br></br>
                    <Button className="loginButton btn-secondary" onClick={processLogin}>Login</Button>
                    <Button className="registerButton btn-secondary" onClick={directToRegister}>Register</Button>       
                </div>       
            </div>
        </>
    )
}
