import React, { useRef } from "react";
import Login from "./Login";
import { root }  from './index';

export default function Register() {
    const usernameInput = useRef()
    const passwordInput = useRef()

    function processRegister(e){
        const usernameText = usernameInput.current.value
        const passwordText = passwordInput.current.value
        console.log(usernameText + " " + passwordText)
        if(usernameText.length !== 0 && passwordText.length !== 0){
            var clientServerOptions = {
                uri: 'http://localhost:9000/users/registered',
                body: JSON.stringify(postData),
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                }
            }
            request(clientServerOptions, function (error, response) {
                console.log(error,response.body);
                return;
            });
            directToLogin();
        }

    }

    function directToLogin(e){
        root.render(
            <React.StrictMode>
                <Login/>
            </React.StrictMode>
        );
    }

    return (
        <>
            <div>REGISTER PAGE</div>
            <div>Username</div>
            <input ref={usernameInput} type ="text"/>
            <div>Password</div>
            <input ref={passwordInput} type ="password"/>
            <button onClick={processRegister}>REGISTER</button>
            <button onClick={directToLogin}>GO TO LOGIN</button>
        </>
    )
}
