import React, { useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";

export default function ChatPage() {
    const navigate = useNavigate();
    const webSocket = useRef(null);

    useEffect(() => {
        console.log("AAA");
        if(localStorage.getItem('token') === null)
            navigate("/") 
        webSocket.current = new WebSocket('ws://localhost:8080/ChatServer/ws');
        webSocket.current.onopen = () => {
            console.log('WebSocket Client Connected');
        };
    
        webSocket.current.onmessage = (message) => {
            console.log(message);
        };
        webSocket.current.onerror = function() {
            console.log('Connection Error');
        };
    }, []);

    return (
        <>
            <div>CHAT PAGE</div>
            <div class="centerMiddle">
                <div class="logo"><b>CHIT-CHAT</b></div>
                <div class="formBack">
                    <br></br>
                    <br></br>
                    <Button className="loginButton btn-secondary" onClick={processLogin}>Login</Button>
                    <Button className="registerButton btn-secondary" onClick={directToRegister}>Register</Button>       
                </div>       
            </div>
            <button onClick={logOut}>Logout</button>
        </>
    )

    function logOut(e){
        localStorage.clear()
        webSocket.current.close(); 
        navigate("/")
    }
}
