import React, { useRef } from "react";
import { useNavigate } from "react-router-dom";
import { Button } from 'react-bootstrap';

export default function Register() {
    const usernameInput = useRef()
    const passwordInput = useRef()
    const passwordInputTwo = useRef()
    const navigate = useNavigate();

    function processRegister(e){
        const usernameText = usernameInput.current.value
        const passwordText = passwordInput.current.value
        if(usernameText.length !== 0 && passwordText.length !== 0){
            const requestOptions = {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json'},
                body:JSON.stringify({ "username": usernameText, "password": passwordText })
            };
            fetch('http://localhost:9000/users/register', requestOptions)
            .then(res => res.text())
            .then(res => console.log(res))
            directToLogin();
        }

    }

    function directToLogin(e){
        navigate("/")
    }

    return (
        <>
            <div class="centerMiddle">
                <div class="logo"><b>CHIT-CHAT</b></div>
                <div class="formBack">
                    <div class="header">Register</div>
                    <br></br>
                    <input  class="form-control inputs" ref={usernameInput} type="text" placeholder="Username"/>
                    <br></br>
                    <input class="form-control inputs" ref={passwordInput} type="password" placeholder="Password"/>
                    <br></br>
                    <input class="form-control inputs" ref={passwordInputTwo} type="password" placeholder="Re"/>
                    <Button className="loginButton btn-secondary" onClick={processRegister}>Register</Button>
                    <Button className="registerButton btn-secondary" onClick={directToLogin}>Login</Button>       
                </div>       
            </div>
        </>
    )
}
