import React, { useRef, useState } from "react";
import Register from "./Register";
import { root }  from './index';
import ChatPage from "./ChatPage";
import Cookies from 'js-cookie'
import {BrowserHistory} from 'react-router';

export default function Login() {
    const usernameInput = useRef()
    const passwordInput = useRef()
    const [status, setStatus] = useState(null);

    function processLogin(e){
        const usernameText = usernameInput.current.value
        const passwordText = passwordInput.current.value

        if(usernameText.length !== 0 && passwordText.length !== 0){
            const requestOptions = {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json'},
                body:JSON.stringify({ "username": usernameText, "password": passwordText })
            };

            fetch('http://localhost:9000/users/login', requestOptions)
            .then(res => setStatus(res.status))
            if(status === 201) directToChatPage(usernameText)
        }
    }

    function directToRegister(e){
        root.render(
            <BrowserHistory>
                <React.StrictMode>
                    <Register/>
                </React.StrictMode>
            </BrowserHistory>
        );
    }
    function directToChatPage(username){
        Cookies.set('username', username)
        root.render(
            <React.StrictMode>
                <ChatPage/>
            </React.StrictMode>
        );
    }

    return (
        <>
            <div>LOGIN PAGE</div>
            <div>Username</div>
            <input ref={usernameInput} type ="text"/>
            <div>Password</div>
            <input ref={passwordInput} type ="password"/>
            <button onClick={processLogin}>Login</button>
            <button onClick={directToRegister}>Register</button>
        </>
    )
}
